function startMove(obj,attrJson,fn){clearInterval(obj.timer);obj.timer=setInterval(function(){var attrValue,speed,isEnd=true;for(var attrName in attrJson){if(attrName==='opacity'){attrValue=Math.round(parseFloat(getStyle(obj,attrName))*100)}else{attrValue=parseInt(getStyle(obj,attrName))}if(attrValue!=attrJson[attrName]){isEnd=false;speed=(attrJson[attrName]-attrValue)/10;speed=speed>0?Math.ceil(speed):Math.floor(speed);speed+=attrValue;if(attrName==='opacity'){obj.style[attrName]=speed/100;obj.style['filter']='alpha(opacity='+speed+')';}else{obj.style[attrName]=speed+'px'}}}if(isEnd){clearInterval(obj.timer);if(typeof fn==="function")fn();}},30);}function getStyle(element,attr){if(element.currentStyle){return element.currentStyle[attr]}else{return getComputedStyle(element,false)[attr]}}